package com.example.tralova.ui.gallery

object TouristData {
    private val nama_wisatawan = arrayOf("Syech", "Kholik Hasan","Tinesh Roy","Dimas Saputra")

    private val detail_penyakit = arrayOf(
        "Flu\nBatuk\nDemam\nMudah Capek",
        "Asma\nmagh\nKolestrol\nGula Rendah",
        "Flu\nBatuk\nDemam\nMudah Capek",
        "Asma\nmagh\nKolestrol\nGula Rendah"
        )

    private val pp_wisatawan = arrayOf("data:image/jpeg;base64,",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTm2Vi8Zg7HRvqvXkRj81vs5-NhxaHuryA2o6EYCRP6o-XzZLzf&s",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQLI_ZJToUm0_o07F7w8NoR3SlyrkOcr9-RsAfA96WO6JF0KK5F&s",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQB_0pTqXb-XQKha_iOJiCgavr43B-ZmsDSu59e-GrHwgUauzvm&s"
    )

    private val obat_penyakit = arrayOf("paramex\nparasetamol", "mylantah\nLemon","paramex\nparasetamol", "mylantah\nLemon")

    private val asal_daerah = arrayOf("Medan", "Binje","Medan", "Binje")

    private val umur_wisatawan = arrayOf("45", "54","45", "54")

    private val Fungsi_Obat =
        arrayOf("Kepala Pusing\nMual-Mual", "Meredahkan Asam Lambung\nMengatasi Kolesterol","Kepala Pusing\nMual-Mual", "Meredahkan Asam Lambung\nMengatasi Kolesterol")

    val listDataWisatawan: ArrayList<HealthCare>
        get() {
            val list = arrayListOf<HealthCare>()
            for (position in nama_wisatawan.indices) {
                val care = HealthCare()
                care.name = nama_wisatawan[position]
                care.Detail = detail_penyakit[position]
                care.pp = pp_wisatawan[position]
                care.Obat = obat_penyakit[position]
                care.asal = asal_daerah[position]
                care.umur = umur_wisatawan[position]
                care.Fungsi = Fungsi_Obat[position]

                list.add(care)
            }
            return list
        }
}